package src;

public class MaxNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1220;
		int b = 240;
		int c = 30;
		
		int ref = 0;
		
		if (a>b) {
		ref= a;
	}
		else {
			ref=b;
		}
		if(ref > c) {
			
			System.out.println("MAX NO is :" +ref);
		}
		else {
			
			System.out.println("MAX NO is :" +c);
		}
		
		}

}
